import * as React from "react";
import classNames from "classnames";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import ReactModal from "react-modal";

export default class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        modalIsOpen: false,
      active: "login",
      surname: "",
      firstname: "",
      patronymic: "",
      email: "",
      password: "",
      onLogin: props.onLogin,
      onRegister: props.onRegister,
    };
  }
  openModal = () => {
    this.setState({ modalIsOpen: true });
  };
  
  closeModal = () => {
    this.setState({ modalIsOpen: false });
  };
  onChangeHandler = (event) => {
    let name = event.target.name;
    let value = event.target.value;
    this.setState({ [name]: value });
  };

  onSubmitLogin = (e) => {
    e.preventDefault();
    if (this.state.password.length > 0) {
      this.state.onLogin(e, this.state.email, this.state.password);
    } else {
      toast.error("Введите пароль!", {
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 3000,
      });
    }
  };

  render() {
    return (
        <ReactModal
        isOpen={this.state.modalIsOpen}
        onRequestClose={this.closeModal}
        contentLabel="Модальное окно"
        // другие пропсы для настройки модального окна
      >
      <div className="flex justify-center">
        <div className="w-1/4">
          <div className="flex justify-center mb-3">
            <h2 className="text-2xl font-bold">Авторизация</h2>
          </div>
          <div className="bg-white p-4 rounded">
            <form onSubmit={this.onSubmitLogin}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700" htmlFor="email">
                  Электронная почта
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  className="w-full border border-gray-300 focus:outline-none focus:border-sky-500 rounded-md px-4 py-2"
                  onChange={this.onChangeHandler}
                  required
                />
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700" htmlFor="password">
                  Пароль
                </label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  className="w-full border border-gray-300 focus:outline-none focus:border-sky-500 rounded-md px-4 py-2"
                  onChange={this.onChangeHandler}
                  required
                />
              </div>

              <button
                type="submit"
                className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none"
              >
                Войти
              </button>
            </form>
          </div>
        </div>
      </div>
      </ReactModal>
    );
  }
}
