import * as React from 'react';
import classNames from 'classnames';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            active: 'login',
            surname: '',
            firstname: '',
            patronymic: '',
            email: '',
            password: '',
            onLogin: props.onLogin,
            onRegister: props.onRegister,
        };
    }

    onChangeHandler = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        this.setState({ [name]: value });
    };

    onSubmitLogin = (e) => {
        e.preventDefault();
        if (this.state.password.length > 0) {
            this.state.onLogin(e, this.state.email, this.state.password);
        }
        else {
            toast.error('Введите пароль!', {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 3000,
            });
        }
    };

    render() {
        return (
            <div className="flex justify-center mt-12">
                <div className="w-1/4">
                    <div className="flex justify-center mb-3">
                    </div>

                    <div className="bg-white p-4">

                        <form onSubmit={this.onSubmitLogin}>
                            <div className="mb-4">
                                <input
                                    type="email"
                                    id="email"
                                    name="email"
                                    className="w-full border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
                                    onChange={this.onChangeHandler}
                                    required
                                />
                                <label className="block text-sm text-gray-700" htmlFor="email">
                                    Электронная почта
                                </label>
                            </div>

                            <div className="mb-4">
                                <input
                                    type="password"
                                    id="password"
                                    name="password"
                                    className="w-full border border-blue-500 focus:outline-blue-600 rounded px-4 py-1"
                                    onChange={this.onChangeHandler}
                                    required
                                />
                                <label className="block text-sm text-gray-700" htmlFor="password">
                                    Пароль
                                </label>
                            </div>

                            <button
                                type="submit"
                                className="w-full bg-blue-600 hover:bg-blue-800 text-white font-bold py-2 px-4 rounded focus:outline-none"
                            >
                                Войти
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}
